import React from 'react';
import { useSelector } from 'react-redux';
import { Button, Input, Select } from 'antd';
import { PlusOutlined, CopyOutlined } from '@ant-design/icons';
import { FanpageStateInterface } from '../../../reducers/setting/interfaces';
import { generateUrlImgFb } from '../../../utils/generate-url-img-fb';
import './style.less';

const { Search } = Input;
const { Option } = Select;

interface IProps {
    defaultPage: string;
    toggle: () => void;
    toggleModalCopy: () => void;
    onSearch: (val: string) => void;
    onChangePage: (page: string) => void;

    style:string;
}

interface IState {
    fanpage: FanpageStateInterface;
}

function QuickMessageHeader({
    defaultPage,
    toggle,
    toggleModalCopy,
    onSearch,
    onChangePage,
}: IProps): JSX.Element {
    const { pages } = useSelector(({ fanpage }: IState) => fanpage);

    const renderFanpages = Object.values(pages).map((fanpage: any) => (
        <Option key={fanpage._id} value={fanpage._id}>
            <img src={generateUrlImgFb(fanpage.fbObjectId, fanpage.accessToken)} alt='' />
            {fanpage.name}
        </Option>
    ));

    return (
        <div className='quick-message-header-wrap header-label style-search'>
            <Select
                style={{ width: 150, marginRight: 10 }}
                defaultValue={defaultPage}
                onSelect={onChangePage}
            >
                {renderFanpages}
            </Select>
            <Button className="btn-hovanco" icon={<PlusOutlined />} onClick={toggle}>
                Thêm
            </Button>
            <Button className="btn-hovanco" icon={<CopyOutlined />} onClick={toggleModalCopy}>
                Copy sang page khác
            </Button>
            <Search className="btn-hovanco"  placeholder='Tìm kiếm tin nhắn...' onSearch={onSearch} allowClear size='large' />
        </div>
    );
}

export default QuickMessageHeader;
